import React from 'react'

export default function(props){
    const status = props.nota >= 7 ? 'Aprovado' : 'Recuperação'
    const notaInt = Math.ceil(props.nota)
    return (
        <div>
            <h2> {props.titulo} </h2>
            <p>
                <strong>{ props.aluno } </strong> 
                tem nota 
                { notaInt }
                e está 
                <strong> { status } ! </strong>
            </p>
        </div>
    )
}
