import React, { Component } from 'react'

class Contador extends Component {
    state = {
        numero: this.props.numeroInicial
    }

    incremento(){
        this.setState({
            numero: this.state.numero + 1
        })
    }

    render() {
        return (
            <div>
                <h2>Contador</h2>
               <button onClick={this.incremento}>+</button>
            </div>
        )
    }

}

export default Contador
